import React from "react";
import Proptypes from "prop-types";

const styles = {
    BackGround: {
        position: "fixed",
        top: 0,
        left: 0,
        height: "100%",
        width:"100%",
        backgroundColor: "#44f",
    },

    footer: {
        bottom: 0,  
        textAlign: "center",
    }
};

const BackGroundCover = (props) => {
    const { BackGroundColor, opacity , widthPercentage} = props;
    const { BackGround } = styles

    styles.BackGround = {
        ...BackGround,
        backgroundColor: BackGround,
        opacity,
        width: widthPercentage
    }
    return ( 
        <div style={props.style}>
            <div style={styles.BackGround}>
                {
                    props.footer? 
                    (
                        <footer style={styles.footer}>
                            {props.footer}
                        </footer>
                    )
                    : null
                }
            </div>

        </div>
    );
};

BackGroundCover.propTypes = {
    leftBackGroundColor: Proptypes.string,
    opacity: Proptypes.number,
    style: Proptypes.object,
    footer: Proptypes.oneOf([Proptypes.node, Proptypes.string])
}

BackGroundCover.defaultProps = {
    leftBackGroundColor: "#44f",
    opacity: 1,
    style: {},
    footer: ''
}

export default BackGroundCover;
